import React, { useState } from "react";
import { useDropzone } from "react-dropzone";
import { uploadImage } from "./api";
import AnnotatedImage from "./AnnotatedImage";
import LegendPanel from "./LegendPanel";

function App() {
  const [file, setFile] = useState(null);
  const [src, setSrc] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState(null);
  const [visibleTypes, setVisibleTypes] = useState([
    "text", "contrast", "button", "heading-hierarchy", "spacing", "alignment"
  ]);

  const onDrop = (acceptedFiles, fileRejections) => {
    if (fileRejections.length) {
      alert("Fichier non valide (type/poids).");
      return;
    }
    const selected = acceptedFiles[0];
    setFile(selected);
    setSrc(null);
    setResult(null);
  };

  const { getRootProps, getInputProps, isDragActive, acceptedFiles } = useDropzone({
    accept: { "image/*": [] },
    multiple: false,
    maxSize: 10 * 1024 * 1024,
    onDrop
  });

  const toggleType = (type) => {
    setVisibleTypes((prev) =>
      prev.includes(type) ? prev.filter(t => t !== type) : [...prev, type]
    );
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Choisis une image !");
      return;
    }

    const fileURL = URL.createObjectURL(file);
    setSrc(fileURL);
    setLoading(true);
    setErrorMsg(null);

    try {
      const data = await uploadImage(file);
      console.log("âœ… DonnÃ©es reÃ§ues du backend :", data);
      setResult(data);
    } catch (error) {
      console.error("Erreur API :", error);
      setErrorMsg("Impossible dâ€™analyser lâ€™image. VÃ©rifie que le backend est dÃ©marrÃ©.");
    }

    setLoading(false);
  };

  return (
    <div style={{ padding: 32, fontFamily: "Arial, sans-serif", maxWidth: 1200, margin: "0 auto" }}>
      <h1 style={{ fontSize: 28, marginBottom: 24 }}>ðŸ§ª UX Audit Tool</h1>

      {/* Zone de drop */}
      <div
        {...getRootProps()}
        style={{
          border: "2px dashed #94a3b8",
          padding: 24,
          borderRadius: 8,
          textAlign: "center",
          background: isDragActive ? "#f1f5f9" : "#fff",
          cursor: "pointer",
          marginBottom: 16
        }}
      >
        <input {...getInputProps()} />
        <p style={{ margin: 0 }}>
          {isDragActive
            ? "DÃ©pose lâ€™image iciâ€¦"
            : "Glisse/dÃ©pose une image ou clique pour sÃ©lectionner"}
        </p>
        {acceptedFiles[0] && (
          <p style={{ marginTop: 8, fontSize: 12, color: "#475569" }}>
            Fichier sÃ©lectionnÃ© : {acceptedFiles[0].name}
          </p>
        )}
      </div>

      {/* Bouton d'analyse */}
      <button
        onClick={handleUpload}
        disabled={loading || !file}
        style={{
          background: loading || !file ? "#cbd5e1" : "#2563eb",
          color: "white",
          fontWeight: 700,
          border: "none",
          borderRadius: 6,
          padding: "10px 16px",
          cursor: loading || !file ? "not-allowed" : "pointer",
          marginBottom: 24
        }}
      >
        {loading ? "Analyse en cours..." : "Analyser"}
      </button>

      {/* Message dâ€™erreur */}
      {errorMsg && <p style={{ color: "red", fontWeight: "bold" }}>{errorMsg}</p>}

      {/* RÃ©sultat */}
      {src && result && (
        <div style={{ display: "flex", gap: 24, marginTop: 32, flexWrap: "wrap" }}>
          <div style={{ flex: 2 }}>
            <AnnotatedImage
              src={src}
              issues={[
                ...(result.groupedTextSizeIssues || []).map(g => ({
                  ...g, type: "text", message: "Texte trop petit"
                })),
                ...(result.groupedContrastIssues || []).map(g => ({
                  ...g, type: "contrast", message: "Contraste insuffisant"
                })),
                ...(result.groupedButtonSizeIssues || []).map(g => ({
                  ...g, type: "button", message: "Bouton trop petit"
                })),
                ...(result.headingIssues || []).map(g => ({
                  ...g, type: "heading-hierarchy", message: "Titre mal hiÃ©rarchisÃ©"
                })),
                ...(result.spacingIssues || []).map(g => ({
                  ...g, type: "spacing", message: "Espacement incohÃ©rent"
                })),
                ...(result.alignmentIssues || []).map(g => ({
                  ...g, type: "alignment", message: "Alignement incohÃ©rent"
                }))
              ].filter(issue => visibleTypes.includes(issue.type))}
            />
          </div>
          <div style={{ flex: 1 }}>
            <LegendPanel visibleTypes={visibleTypes} toggleType={toggleType} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
