import React, { useState } from "react";
import { useDropzone } from "react-dropzone";
import { uploadImage } from "./api";
import AnnotatedImage from "./AnnotatedImage";
import LegendPanel from "./LegendPanel";

function App() {
  const [file, setFile] = useState(null);
  const [src, setSrc] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState(null);

  // types visibles (cochés dans la légende)
  const [visibleTypes, setVisibleTypes] = useState([
    "text", "contrast", "button", "heading-hierarchy", "spacing", "alignment"
  ]);

  const toggleType = (type) => {
    setVisibleTypes((prev) =>
      prev.includes(type) ? prev.filter(t => t !== type) : [...prev, type]
    );
  };

  // Dropzone
  const onDrop = (acceptedFiles, fileRejections) => {
    if (fileRejections.length) {
      alert("Fichier non valide (type ou taille).");
      return;
    }
    const selected = acceptedFiles[0];
    setFile(selected);
    setSrc(null);
    setResult(null);
  };

  const { getRootProps, getInputProps, isDragActive, acceptedFiles } = useDropzone({
    accept: { "image/*": [] },
    multiple: false,
    maxSize: 10 * 1024 * 1024,
    onDrop
  });

  const handleUpload = async () => {
    if (!file) return;

    const fileURL = URL.createObjectURL(file);
    setSrc(fileURL);
    setLoading(true);
    setErrorMsg(null);

    try {
      const data = await uploadImage(file);
      setResult(data);
    } catch (error) {
      console.error("Erreur API :", error);
      setErrorMsg("Impossible d’analyser l’image. Vérifie que le backend est démarré.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.page}>
      {/* Header */}
      <header style={styles.header}>
        <div style={styles.brand}>
          <span style={styles.logo} role="img" aria-label="loupe">🔍</span>
          <h1 style={styles.title}>UX Audit Tool</h1>
        </div>
      </header>

      {/* Bloc upload + actions */}
      <section style={styles.panel}>
        <div {...getRootProps({ style: {
          ...styles.dropzone,
          borderColor: isDragActive ? "#3b82f6" : "#cbd5e1",
          background: isDragActive ? "#f1f5f9" : "#fff"
        }})}>
          <input {...getInputProps()} />
          <p style={styles.dropText}>
            {isDragActive
              ? "Dépose l’image ici…"
              : "Glisse/dépose une image ou clique pour sélectionner"}
          </p>
          {acceptedFiles[0] && (
            <p style={styles.fileName}>Fichier sélectionné : {acceptedFiles[0].name}</p>
          )}
        </div>

        <div style={styles.actions}>
          <button
            onClick={handleUpload}
            disabled={loading || !file}
            style={{
              ...styles.primaryBtn,
              ...(loading || !file ? styles.btnDisabled : {})
            }}
          >
            {loading ? "Analyse en cours…" : "Analyser"}
          </button>

          {errorMsg && <p style={styles.error}>{errorMsg}</p>}
        </div>
      </section>

      {/* Contenu principal : 2 colonnes */}
      {src && result && (
        <main style={styles.mainGrid}>
          <div style={styles.canvasCard}>
            {/* conteneur scrollable pour l’image annotée */}
            <div style={styles.canvasScroll}>
              <AnnotatedImage
                src={src}
                issues={[
                  ...(result.groupedTextSizeIssues || []).map(g => ({
                    ...g, type: "text", message: "Texte trop petit"
                  })),
                  ...(result.groupedContrastIssues || []).map(g => ({
                    ...g, type: "contrast", message: "Contraste insuffisant"
                  })),
                  ...(result.groupedButtonSizeIssues || []).map(g => ({
                    ...g, type: "button", message: "Bouton trop petit"
                  })),
                  ...(result.headingIssues || []).map(g => ({
                    ...g, type: "heading-hierarchy", message: "Titre mal hiérarchisé"
                  })),
                  ...(result.spacingIssues || []).map(g => ({
                    ...g, type: "spacing", message: "Espacement incohérent"
                  })),
                  ...(result.alignmentIssues || []).map(g => ({
                    ...g, type: "alignment", message: "Alignement incohérent"
                  }))
                ].filter(issue => visibleTypes.includes(issue.type))}
              />
            </div>
          </div>

          <aside style={styles.sidebar}>
            <div style={styles.card}>
              <LegendPanel visibleTypes={visibleTypes} toggleType={toggleType} />
            </div>
          </aside>
        </main>
      )}
    </div>
  );
}

/* ================== Styles ================== */
const styles = {
  page: {
    fontFamily: "Inter, system-ui, -apple-system, Segoe UI, Roboto, Arial, sans-serif",
    background: "#f8fafc",
    minHeight: "100vh",
    color: "#0f172a",
    padding: "24px"
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 12
  },
  brand: { display: "flex", alignItems: "center", gap: 10 },
  logo: { fontSize: 24 },
  title: { margin: 0, fontSize: 22, fontWeight: 800, letterSpacing: 0.2 },

  panel: {
    background: "#ffffff",
    border: "1px solid #e2e8f0",
    borderRadius: 12,
    padding: 16,
    boxShadow: "0 1px 2px rgba(0,0,0,0.03)",
    marginBottom: 16
  },
  dropzone: {
    border: "2px dashed #cbd5e1",
    borderRadius: 10,
    padding: 18,
    textAlign: "center",
    transition: "all .15s ease",
    cursor: "pointer"
  },
  dropText: { margin: 0, color: "#475569" },
  fileName: { marginTop: 8, fontSize: 12, color: "#64748b" },
  actions: { display: "flex", alignItems: "center", gap: 12, marginTop: 12 },

  primaryBtn: {
    background: "#2563eb",
    color: "white",
    fontWeight: 700,
    border: "none",
    borderRadius: 8,
    padding: "10px 16px",
    cursor: "pointer",
    boxShadow: "0 1px 0 rgba(0,0,0,0.05)"
  },
  btnDisabled: { background: "#cbd5e1", cursor: "not-allowed" },
  error: { color: "#b91c1c", margin: 0 },

  mainGrid: {
    display: "grid",
    gridTemplateColumns: "1fr 280px",
    gap: 16,
    alignItems: "start"
  },
  canvasCard: {
    background: "#fff",
    border: "1px solid #e2e8f0",
    borderRadius: 12,
    boxShadow: "0 1px 2px rgba(0,0,0,0.03)",
    padding: 8,
    minHeight: 200
  },
  canvasScroll: {
    overflow: "auto",
    maxHeight: "70vh",
    borderRadius: 8
  },
  sidebar: { position: "sticky", top: 24 },
  card: {
    background: "#fff",
    border: "1px solid #e2e8f0",
    borderRadius: 12,
    boxShadow: "0 1px 2px rgba(0,0,0,0.03)",
    padding: 12
  }
};

export default App;
