import React from "react";
import { Stage, Layer, Image as KonvaImage, Rect, Text, Group } from "react-konva";
import useImage from "use-image";

export default function AnnotatedImage({ src, issues, containerWidth }) {
  const [image] = useImage(src);

  const colors = {
    text: "orange",
    contrast: "red",
    button: "blue",
    "heading-hierarchy": "purple",
    spacing: "green",
    alignment: "pink"
  };

  if (!image) return <p>Chargement de l'image...</p>;
  const scale = containerWidth ? Math.min(1, containerWidth / image.width) : 1;

  return (
    <Stage width={image.width * scale} height={image.height * scale} scaleX={scale} scaleY={scale}>
      <Layer>
        <KonvaImage image={image} />
        {issues.map((issue, i) => (
          <Group key={i}>
            <Rect
              x={issue.boundingBox.x}
              y={issue.boundingBox.y}
              width={issue.boundingBox.w}
              height={issue.boundingBox.h}
              stroke={colors[issue.type] || "black"}
              strokeWidth={issue.severity === "high" ? 3 : issue.severity === "medium" ? 2 : 1}
              dash={[4, 4]}
            />
            <Text
              x={issue.boundingBox.x}
              y={Math.max(issue.boundingBox.y - 18, 0)}
              text={issue.message}
              fontSize={14}
              fill={colors[issue.type] || "black"}
            />
          </Group>
        ))}
      </Layer>
    </Stage>
  );
}
